mapper.update:
UPDATE C_COMMUNITY
SET CCOMU_HIT = ccomu_hit + 1
WHERE ccomu_bno = #{ccomu_bno}

mapper.select:
SELECT COUNT(*)
FROM C_COMMENTS_LIKE
WHERE CCOMU_BNO = #{ccomu_bno} AND CCOMM_ID = #{ccomm_id} AND COM_ID = #{com_id}

mapper.update:
UPDATE C_COMMENTS
SET CCOMU_GOOD = CCOMU_GOOD + 1
WHERE CCOMU_BNO = #{ccomu_bno} AND CCOMM_ID = #{ccomm_id}

UPDATE C_COMMENTS
SET CCOMU_GOOD = ccomu_good - 1
WHERE CCOMU_BNO = #{ccomu_bno} AND CCOMM_ID = #{ccomm_id}

		 UPDATE  C_COMMUNITY
     SET    CCOMU_TITLE    =  #{ccomu_title},
            CCOMU_CONTENT   =  #{ccomu_content}
     WHERE  CCOMU_BNO      =  #{ccomu_bno}
